/* eslint-disable no-redeclare, no-new-wrappers */
'use strict';

// MODULES //

var tape = require( 'tape' );
var isFinite = require( './../lib/object.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isFinite, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a number object having a finite value', function test( t ) {
	t.strictEqual( isFinite( new Number( 5.0 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a primitive number, even if the number is a finite value', function test( t ) {
	t.strictEqual( isFinite( 3.0 ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided a finite number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		1.0/0.0,
		-1.0/0.0,
		NaN,
		new Number( 1.0/0.0 ),
		new Number( -1.0/0.0 ),
		new Number( NaN ),
		null,
		true,
		false,
		void 0,
		[],
		{},
		new Date(),
		/./,
		new RegExp( '.' ),
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isFinite( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
