'use strict';

// MAIN //

/**
* Tests if a value is a gzipped buffer.
*
* @param {*} value - value to test
* @returns {boolean} boolean indicating whether value is a gzipped buffer
*
* @example
* var ArrayBuffer = require( '@stdlib/array/buffer' );
*
* var bool = isgzipBuffer( new ArrayBuffer( 10 ) );
* // returns false
*
* @example
* var bool = isgzipBuffer( [] );
* // returns false
*/
function isgzipBuffer( value ) {
	if ( !value || value.length < 3 ) {
		return false;
	}
	return value[ 0 ] === 0x1F && value[ 1 ] === 0x8B && value[ 2 ] === 0x08;
}


// EXPORTS //

module.exports = isgzipBuffer;
