tape( 'the function returns `false` if not provided a gzip buffer', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		null,
		undefined,
		[],
		{},
		function noop() {},
		new Array( 10 ),
		new Float64Array( 10 ),
		new Float32Array( 10 ),
		new Uint32Array( 10 ),
		new Int32Array( 10 ),
		new Uint16Array( 10 ),
		new Int16Array( 10 ),
		new Uint8Array( 10 ),
		new Int8Array( 10 ),
		new Uint8ClampedArray( 10 ),
		new ArrayBuffer( 10 )
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isgzipBuffer( values[i] ), false, 'returns false when provided ' + values[i] );
	}
	t.end();
});
