'use strict';

// MODULES //

var tape = require( 'tape' );
var isNegativeIntegerArray = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNegativeIntegerArray, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function tests for an array-like object containing only negative integer values', function test( t ) {
	var arr;

	arr = [ -5.0, new Number( -5 ), -1.0 ];
	t.equal( isNegativeIntegerArray( arr ), true, 'returns true' );

	arr = new Int32Array( [-2,-9,-4] );
	t.equal( isNegativeIntegerArray( arr ), true, 'returns true' );

	arr = {'length': 2, '0': new Number( -5 ), '1': new Number( -2 )};
	t.equal( isNegativeIntegerArray( arr ), true, 'returns true' );

	arr = [ -5.0, '3', null ];
	t.equal( isNegativeIntegerArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for an array-like object containing only negative primitive integers', function test( t ) {
	var arr;

	arr = [ -5.0, -1.0 ];
	t.equal( isNegativeIntegerArray.primitives( arr ), true, 'returns true' );

	arr = new Int32Array( [-2,-9,-4] );
	t.equal( isNegativeIntegerArray.primitives( arr ), true, 'returns true' );

	arr = {'length': 2, '0': new Number( -5 ), '1': new Number( -2 )};
	t.equal( isNegativeIntegerArray.primitives( arr ), false, 'returns false' );

	arr = [ new Number( -5 ), -1.0, -1.0 ];
	t.equal( isNegativeIntegerArray.primitives( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for an array-like object containing only Number objects having negative integer values', function test( t ) {
	var arr;

	arr = [ new Number( -5 ), new Number( -5 ) ];
	t.equal( isNegativeIntegerArray.objects( arr ), true, 'returns true' );

	arr = [ -5, -3 ];
	t.equal( isNegativeIntegerArray.objects( arr ), false, 'returns false' );

	arr = {'length': 2, '0': new Number( -5 ), '1': new Number( -2 )};
	t.equal( isNegativeIntegerArray.objects( arr ), true, 'returns true' );

	arr = new Int32Array( [-2,-9,-4] );
	t.equal( isNegativeIntegerArray.objects( arr ), false, 'returns false' );

	t.end();
});
