/* eslint-disable no-new-wrappers */
'use strict';

var isNumber = require( './../lib' );

console.log( isNumber( 5 ) );
// => true

console.log( isNumber( new Number( 5 ) ) );
// => true

console.log( isNumber( NaN ) );
// => true

console.log( isNumber( '5' ) );
// => false

console.log( isNumber( null ) );
// => false
