'use strict';

// MODULES //

var tape = require( 'tape' );
var isOdd = require( './../lib/generic.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isOdd, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided an odd number', function test( t ) {
	t.equal( isOdd( 5.0 ), true, 'returns true' );
	t.equal( isOdd( new Number( 5 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided an odd number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		4.0,
		3.14,
		null,
		true,
		undefined,
		[],
		{},
		function(){},
		Number.POSITIVE_INFINITY,
		Number.NEGATIVE_INFINITY,
		NaN
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isOdd( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
