'use strict';

var isEven = require( '@stdlib/math/base/assert/is-even' );
var typedarrayfcn = require( './../lib' );

var arr1;
var arr2;
var bool;
var f;
var i;

arr1 = new Int32Array( 25 );
for ( i = 0; i < arr1.length; i++ ) {
	arr1[ i ] = i;
}

arr2 = new Int32Array( 25 );
for ( i = 0; i < arr2.length; i++ ) {
	arr2[ i ] = 2 * i;
}

f = typedarrayfcn( isEven );

bool = f( arr1 );
console.log( bool );
// => false

bool = f( arr2 );
console.log( bool );
// => true
