'use strict';

// MODULES //

var isTypedArray = require( '@stdlib/assert/is-typed-array' );


// MAIN //

/**
* Tests each typed array element.
*
* @param {Function} fcn - function to apply
* @param {*} value - value to test
* @returns {boolean} boolean indicating if a value is a typed array for which every element passes a test condition
*/
function every( fcn, value ) {
	var len;
	var i;
	if ( typeof fcn !== 'function' ) {
		throw new TypeError( 'invalid input argument. First argument must be a function. Value: `' + fcn + '`.' );
	}
	if ( !isTypedArray( value ) ) {
		return false;
	}
	len = value.length;
	if ( !len ) {
		return false;
	}
	for ( i = 0; i < len; i++ ) {
		if ( fcn( value[ i ] ) === false ) {
			return false;
		}
	}
	return true;
} // end FUNCTION every()


// EXPORTS //

module.exports = every;
