'use strict';

/**
* Test if every element of a typed array passes a test condition.
*
* @module @stdlib/assert/tools/typed-array-function
*
* @example
* var isOdd = require( '@stdlib/math/base/utils/is-odd' );
* var validate = require( '@stdlib/assert/tools/typed-array-function' );
*
* var arr1 = new Int32Array( [ 1, 3, 5, 7 ] );
* var arr2 = new Int32Array( [ 3, 5, 6, 8 ] );
*
* var bool = validate( isOdd, arr1 );
* // returns true
*
* bool = validate( isOdd, arr2 );
* // returns false
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var every = require( './every.js' );
var raw = require( './every.raw.js' );
var create = require( './create.js' );


// MAIN //

setReadOnly( every, 'raw', raw );
setReadOnly( every, 'create', create );


// EXPORTS //

module.exports = every;
