#include "stdlib/blas/base/csscal.h"
#include "stdlib/complex/float32.h"
#include "stdlib/math/base/ops/cmulf.h"
#include "stdlib/complex/realf.h"
#include "stdlib/complex/imagf.h"

/**
* Scales a complex single-precision floating-point vector by a single-precision real floating-point constant.
*
* @param N        number of indexed elements
* @param alpha    scalar constant
* @param X        input array
* @param strideX  stride length
*/
void c_csscal( const int N, const float *alpha, void *X, const int strideX ) {
	stdlib_complex64_t *x = (stdlib_complex64_t *)X;
    stdlib_complex64_t z;
    float re, im;
    int i, nincx;
    uint8_t *ip1 = (uint8_t *)x;
    int64_t is1 = 8 * strideX;

    if ( N <= 0 || strideX <= 0 || *alpha == 1.0f ) {
        return;
    }

    if ( strideX == 1 ) {
        for ( i = 0; i < N; i++, ip1 += is1 ) {
            z = *( (stdlib_complex64_t *)ip1 );
            re = stdlib_realf( z ) * (*alpha);
            im = stdlib_imagf( z ) * (*alpha);
            *( (stdlib_complex64_t *)ip1 ) = stdlib_complex64( re, im );
        }
    }else {
        nincx = N * strideX;
        for ( i = 0; i < nincx; i += strideX ) {
            z = *( (stdlib_complex64_t *)ip1 );
            re = stdlib_realf( z ) * (*alpha);
            im = stdlib_imagf( z ) * (*alpha);
            *( (stdlib_complex64_t *)ip1 ) = stdlib_complex64( re, im );
			ip1 += is1;
        }
    }
    return;
}
