#include "stdlib/blas/base/drotg.h"
#include "stdlib/blas/base/shared.h"
#include "stdlib/math/base/special/copysign.h"
#include "stdlib/math/base/special/abs2.h"
#include "stdlib/math/base/special/sqrt.h"
#include "stdlib/math/base/special/abs.h"

/**
* Constructs a Givens plane rotation.
*
* @param a        rotational elimination parameter
* @param b        rotational elimination parameter
* @param out      output array
* @param stride   index increment
* @param offset   starting index
*/
void API_SUFFIX(c_drotg)( double* a, double* b, double* c, double* s ) {
	double scale;
	double sign;
	double aa;
	double ab;
	double r;
	double z;

	aa = stdlib_base_abs( *a );
	ab = stdlib_base_abs( *b );
	if ( aa > ab ) {
		sign = stdlib_base_copysign( 1.0, *a );
	} else {
		sign = stdlib_base_copysign( 1.0, *b );
	}
	scale = aa + ab;
	if ( scale == 0.0 ) {
		*c = 1.0;
		*s = 0.0;
		r = 0.0;
		z = 0.0;
	} else {
		r = scale * stdlib_base_sqrt( stdlib_base_abs2( *a/scale ) + stdlib_base_abs2( *b/scale ) );
		r *= sign;
		*c = *a / r;
		*s = *b / r;
		z = 1.0;
		if ( aa > ab ) {
			z = *s;
		} else if ( *c != 0.0 ) {
			z = 1.0 / *c;
		}
	}
	*a = r;
	*b = z;
	return;
}
