// TypeScript Version: 4.1

/// <reference types="@stdlib/types"/>

import { Layout, MatrixTriangle } from '@stdlib/types/blas';

/**
* Interface describing `dspr2`.
*/
interface Routine {
	/**
	* Performs the symmetric rank 2 operation `A = α*x*y^T + α*y*x^T + A` where `α` is a scalar, `x` and `y` are `N` element vector, and `A` is an `N` by `N` symmetric matrix supplied in packed form.
	*
	* @param order - storage layout
	* @param uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is supplied
	* @param N - number of elements along each dimension of `A`
	* @param alpha - scalar constant
	* @param x - first input vector
	* @param strideX - `x` stride length
	* @param y - second input vector
	* @param strideX - `y` stride length
	* @param AP - packed form of a symmetric matrix `A`
	* @returns `AP`
	*
	* @example
	* var Float64Array = require( '@stdlib/array/float64' );
	*
	* var AP = new Float64Array( [ 1.0, 2.0, 3.0, 1.0, 2.0, 1.0 ] ); // => [ [ 1.0, 2.0, 3.0 ], [ 0.0, 1.0, 2.0 ], [ 0.0, 0.0, 1.0 ] ]
	* var x = new Float64Array( [ 1.0, 2.0, 3.0 ] );
	* var y = new Float64Array( [ 1.0, 2.0, 3.0 ] );
	*
	* dspr2( 'row-major', 'upper', 3, 1.0, x, 1, y, 1, AP );
	* // AP => <Float64Array>[ 3.0, 6.0, 9.0, 9.0, 14.0, 19.0 ]
	*/
	( order: Layout, uplo: MatrixTriangle, N: number, alpha: number, x: Float64Array, strideX: number, y: Float64Array, strideY: number, AP: Float64Array ): Float64Array;

	/**
	* Performs the symmetric rank 2 operation `A = α*x*y^T + α*y*x^T + A`, using alternative indexing semantics and where `α` is a scalar, `x` and `y` are `N` element vector, and `A` is an `N` by `N` symmetric matrix supplied in packed form.
	*
	* @param order - storage layout
	* @param uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is supplied
	* @param N - number of elements along each dimension of `A`
	* @param alpha - scalar constant
	* @param x - first input vector
	* @param strideX - `x` stride length
	* @param offsetX - starting index for `x`
	* @param y - second input vector
	* @param strideY - `y` stride length
	* @param offsetY - starting index for `y`
	* @param AP - packed form of a symmetric matrix `A`
	* @param strideAP - `AP` stride length
	* @param offsetAP - starting index for `AP`
	* @returns `AP`
	*
	* @example
	* var Float64Array = require( '@stdlib/array/float64' );
	*
	* var AP = new Float64Array( [ 1.0, 2.0, 3.0, 1.0, 2.0, 1.0 ] ); // => [ [ 1.0, 2.0, 3.0 ], [ 0.0, 1.0, 2.0 ], [ 0.0, 0.0, 1.0 ] ]
	* var x = new Float64Array( [ 1.0, 2.0, 3.0 ] );
	* var y = new Float64Array( [ 1.0, 2.0, 3.0 ] );
	*
	* dspr2.ndarray( 'row-major', 'upper', 3, 1.0, x, 1, 0, y, 1, 0, AP, 1, 0 );
	* // AP => <Float64Array>[ 3.0, 6.0, 9.0, 9.0, 14.0, 19.0 ]
	*/
	ndarray( order: Layout, uplo: MatrixTriangle, N: number, alpha: number, x: Float64Array, strideX: number, offsetX: number, y: Float64Array, strideY: number, offsetY: number, AP: Float64Array, strideAP: number, offsetAP: number ): Float64Array;
}

/**
* Performs the symmetric rank 2 operation `A = α*x*y^T + α*y*x^T + A` where `α` is a scalar, `x` and `y` are `N` element vector, and `A` is an `N` by `N` symmetric matrix supplied in packed form.
*
* @param order - storage layout
* @param uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is supplied
* @param N - number of elements along each dimension of `A`
* @param alpha - scalar constant
* @param x - first input vector
* @param strideX - `x` stride length
* @param y - second input vector
* @param strideY - `y` stride length
* @param AP - packed form of a symmetric matrix `A`
* @returns `AP`
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var AP = new Float64Array( [ 1.0, 2.0, 1.0, 3.0, 2.0, 1.0 ] );
* var x = new Float64Array( [ 1.0, 2.0, 3.0 ] );
* var y = new Float64Array( [ 1.0, 2.0, 3.0 ] );
*
* dspr2( 'column-major', 'upper', 3, 1.0, x, 1, y, 1, AP );
* // AP => <Float64Array>[ 3.0, 6.0, 9.0, 9.0, 14.0, 19.0 ]
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var AP = new Float64Array( [ 1.0, 2.0, 1.0, 3.0, 2.0, 1.0 ] );
* var x = new Float64Array( [ 1.0, 2.0, 3.0 ] );
* var y = new Float64Array( [ 1.0, 2.0, 3.0 ] );
*
* dspr2.ndarray( 'column-major', 'upper', 3, 1.0, x, 1, 0, y, 1, 0, AP, 1, 0 );
* // AP => <Float64Array>[ 3.0, 6.0, 9.0, 9.0, 14.0, 19.0 ]
*/
declare var dspr2: Routine;


// EXPORTS //

export = dspr2;
