import dtpsv = require( './index' );


// TESTS //

// The function returns a Float64Array...
{
	const x = new Float64Array( 10 );
	const AP = new Float64Array( 55 );

	dtpsv( 'row-major', 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectType Float64Array
}

// The compiler throws an error if the function is provided a first argument which is not a string...
{
	const x = new Float64Array( 10 );
	const AP = new Float64Array( 55 );

	dtpsv( 10, 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectError
	dtpsv( true, 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectError
	dtpsv( false, 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectError
	dtpsv( null, 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectError
	dtpsv( undefined, 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectError
	dtpsv( [], 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectError
	dtpsv( {}, 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectError
	dtpsv( ( x: number ): number => x, 'upper', 'no-transpose', 'unit', 10, AP, x, 1 ); // $ExpectError
}

