// The compiler throws an error if the function is provided an unsupported number of arguments...
{
	const x = new Float64Array( 10 );
	const AP = new Float64Array( 55 );

	dtpsv(); // $ExpectError
	dtpsv( 'row-major' ); // $ExpectError
	dtpsv( 'row-major', 'upper' ); // $ExpectError
	dtpsv( 'row-major', 'upper', 'no-transpose' ); // $ExpectError
	dtpsv( 'row-major', 'upper', 'no-transpose', 'unit' ); // $ExpectError
	dtpsv( 'row-major', 'upper', 'no-transpose', 'unit', 10 ); // $ExpectError
	dtpsv( 'row-major', 'upper', 'no-transpose', 'unit', 10, AP ); // $ExpectError
	dtpsv( 'row-major', 'upper', 'no-transpose', 'unit', 10, AP, x ); // $ExpectError
	dtpsv( 'row-major', 'upper', 'no-transpose', 'unit', 10, AP, x, 1, 1 ); // $ExpectError
}

// Attached to main export is an `ndarray` method which returns a Float64Array...
{
	const x = new Float64Array( 10 );
	const AP = new Float64Array( 55 );

	dtpsv.ndarray( 'row-major', 'upper', 'no-transpose', 'unit', 10, AP, 1, 0, x, 1, 0 ); // $ExpectType Float64Array
}

