// TypeScript Version: 4.1

/// <reference types="@stdlib/types"/>

import { NumericArray } from '@stdlib/types/array';

/**
* Interface describing `igamax`.
*/
interface Routine {
	/**
	* Finds the index of the first element having maximum absolute value.
	*
	* @param N - number of indexed elements
	* @param x - first input array
	* @param strideX - `x` stride length
	* @returns index value
	*
	* @example
	* var x = [ -2.0, 1.0, 3.0, -5.0, 4.0, 0.0, -1.0, -3.0 ];
	*
	* var idx = igamax( x.length, x, 1 );
	* // returns 3
	*/
	( N: number, x: NumericArray, strideX: number ): number;

	/**
	* Finds the index of the first element having maximum absolute value using alternative indexing semantics.
	*
	* @param N - number of indexed elements
	* @param x - first input array
	* @param strideX - `x` stride length
	* @param offsetX - starting index for `x`
	* @returns index value
	*
	* @example
	* var x = [ -2.0, 1.0, 3.0, -5.0, 4.0, 0.0, -1.0, -3.0 ];
	*
	* var idx = igamax.ndarray( x.length, x, 1, 0 );
	* // returns 3
	*/
	ndarray( N: number, x: NumericArray, strideX: number, offsetX: number ): number;
}

/**
* Finds the index of the first element having maximum absolute value.
*
* @param N - number of indexed elements
* @param x - first input array
* @param strideX - `x` stride length
* @returns index value
*
* @example
* var x = [ -2.0, 1.0, 3.0, -5.0, 4.0, 0.0, -1.0, -3.0 ];
*
* var idx = igamax( 4, x, 2 );
* // returns 2
*
* @example
* var x = [ -2.0, 1.0, 3.0, -5.0, 4.0, 0.0, -1.0, -3.0 ];
*
* var idx = igamax.ndarray( x.length, x, 1, 1 );
* // returns 2
*/
declare var igamax: Routine;


// EXPORTS //

export = igamax;
