'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var randu = require( '@stdlib/random/base/randu' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var pow = require( '@stdlib/math/base/special/pow' );
var Float32Array = require( '@stdlib/array/float32' );
var pkg = require( './../package.json' ).name;
var sdot = require( './../lib/main.js' );


// FUNCTIONS //

/**
* Creates 2 random arrays and a benchmark function to benchmark against them.
*
* @private
* @param {PositiveInteger} len - array length
* @returns {Function} benchmark function
*/
function createBenchmark( len ) {
	var x;
	var y;
	var i;

	x = new Float32Array( len );
	y = new Float32Array( len );
	for ( i = 0; i < x.length; i++ ) {
		x[ i ] = ( randu()*10000.0 ) - 20000.0;
		y[ i ] = ( randu()*10000.0 ) - 20000.0;
	}
	return benchmark;

	function benchmark( b ) {
		var i;
		var d;

		i = 0;
		b.tic();
		return next();

		function next() {
			i += 1;
			if ( i <= b.iterations ) {
				d = sdot( x.length, x, 1, y, 1);
				if ( isnan( d ) ) {
					b.fail( 'something went wrong, returned ' + d );
				}
				return setTimeout( next, 0 );
			}
			b.toc();
			if ( isnan( d ) ) {
				b.fail( 'something went wrong' );
			} else {
				b.pass( 'benchmark completed' );
			}
			b.end();
		}
	}
}


// MAIN //

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var len;
	var min;
	var max;
	var f;
	var i;

	min = 1; // 10^min
	max = 6; // 10^max

	for ( i = min; i <= max; i++ ) {
		len = pow( 10, i );
		f = createBenchmark( len );
		bench( pkg+':len='+len, f );
	}
}

main();
