!> Wraps the sdot function as a subroutine

! @param {integer} N - Number of values to use from each array
! @param {Array<real>} x - First array
! @param {integer} strideX - Stride length on the first array
! @param {Array<real>} y - Second array
! @param {integer} strideY - Stride length of the second array
! @param {real} sum - The dot product of x and y
!<
subroutine sdotsum( N, sx, strideX, sy, strideY, dot )
  implicit none
  ! ..
  ! External functions:
  interface
    real function sdot( N, sx, strideX, sy, strideY )
      integer :: strideX, strideY, N
      real :: sx(*), sy(*)
    end function sdot
  end interface
  ! ..
  ! Scalar arguments:
  integer :: strideX, strideY, N
  real :: dot
  ! ..
  ! Array arguments:
  real :: sx(*), sy(*)
  ! ..
  dot = sdot( N, sx, strideX, sy, strideY )
  return
end subroutine
