#include "stdlib/blas/base/srotg.h"
#include "stdlib/math/base/special/absf.h"
#include "stdlib/math/base/special/sqrtf.h"
#include "stdlib/math/base/special/abs2f.h"
#include "stdlib/math/base/special/copysignf.h"

/**
* Constructs a Givens plane rotation.
*
* @param a    rotational elimination parameter
* @param b    rotational elimination parameter
* @param c    cosine of the rotation angle
* @param s    sine of the rotation angle
*/
void c_srotg( float *a, float *b, float *c, float *s ) {
    float scale;
    float sign;
    float aa;
    float ab;
    float r;
    float z;

    aa = stdlib_base_absf( *a );
    ab = stdlib_base_absf( *b );
    if ( aa > ab ) {
        sign = stdlib_base_copysignf( 1.0f, *a );
    } else {
        sign = stdlib_base_copysignf( 1.0f, *b );
    }
    scale = aa + ab;
    if ( scale == 0.0 ) {
        *c = 1.0f;
        *s = 0.0f;
        r = 0.0f;
        z = 0.0f;
    } else {
        r = scale * stdlib_base_sqrtf( stdlib_base_abs2f( *a/scale ) + stdlib_base_abs2f( *b/scale ) );
        r = sign * r;
        *c = *a / r;
        *s = *b / r;
        z = 1.0f;
        if ( aa > ab ) {
            z = *s;
        } else if ( *c != 0.0f ) {
            z = 1.0f / *c;
        }
    }
    *a = r;
    *b = z;
    return;
}
