// The compiler throws an error if the function is provided an unsupported number of arguments...
{
	const x = new Float32Array( 10 );
	const y = new Float32Array( 10 );
	const A = new Float32Array( 55 );

	sspr2(); // $ExpectError
	sspr2( 'row-major' ); // $ExpectError
	sspr2( 'row-major', 'upper' ); // $ExpectError
	sspr2( 'row-major', 'upper', 10 ); // $ExpectError
	sspr2( 'row-major', 'upper', 10, 1.0 ); // $ExpectError
	sspr2( 'row-major', 'upper', 10, 1.0, x ); // $ExpectError
	sspr2( 'row-major', 'upper', 10, 1.0, x, 1 ); // $ExpectError
	sspr2( 'row-major', 'upper', 10, 1.0, x, 1, y ); // $ExpectError
	sspr2( 'row-major', 'upper', 10, 1.0, x, 1, y, 1 ); // $ExpectError
	sspr2( 'row-major', 'upper', 10, 1.0, x, 1, y, 1, A, 10 ); // $ExpectError
}

