#include "stdlib/blas/base/ssyr2.h"
#include "stdlib/blas/base/shared.h"

/**
* Performs the symmetric rank 2 operation `A = α*x*y^T + α*y*x^T + A`.
*
* @param order    storage layout
* @param uplo     specifies whether the upper or lower triangular part of the symmetric matrix `A` should be referenced
* @param N        number of elements along each dimension of `A`
* @param alpha    scalar
* @param x        first input vector
* @param strideX  `x` stride length
* @param y        second input vector
* @param strideY  `y` stride length
* @param A        input matrix
* @param LDA      stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
*/
void API_SUFFIX(c_ssyr2)( const CBLAS_LAYOUT order, const CBLAS_UPLO uplo, const CBLAS_INT N, const float alpha, const float *X, const CBLAS_INT strideX, const float *Y, const CBLAS_INT strideY, float *A, const CBLAS_INT LDA ) {
	CBLAS_INT ix0;
	CBLAS_INT ix1;
	CBLAS_INT iy0;
	CBLAS_INT iy1;
	CBLAS_INT i0;
	CBLAS_INT i1;
	CBLAS_INT ox;
	CBLAS_INT oy;
	float tmp1;
	float tmp2;

	if ( N == 0 || alpha == 0.0f ) {
		return;
	}
	ox = STDLIB_BLAS_BASE_STRIDE2OFFSET( N, strideX );
	oy = STDLIB_BLAS_BASE_STRIDE2OFFSET( N, strideY );
	ix1 = ox;
	iy1 = oy;
	if (
		( order == CblasRowMajor && uplo == CblasLower ) ||
		( order == CblasColMajor && uplo == CblasUpper )
	) {
		for ( i1 = 0; i1 < N; i1++ ) {
			if ( ( X[ ix1 ] != 0.0f ) || ( Y[ iy1 ] != 0.0f ) ) {
				tmp1 = alpha * Y[ iy1 ];
				tmp2 = alpha * X[ ix1 ];
				ix0 = ox;
				iy0 = oy;
				for ( i0 = 0; i0 <= i1; i0++ ) {
					A[ (LDA*i1)+i0 ] += ( X[ ix0 ] * tmp1 ) + ( Y[ iy0 ] * tmp2 ); // eslint-disable-line max-len
					ix0 += strideX;
					iy0 += strideY;
				}
			}
			ix1 += strideX;
			iy1 += strideY;
		}
		return;
	}
	// ( order == CblasRowMajor && uplo == CblasUpper ) || ( order == CblasColMajor && uplo == CblasLower )
	for ( i1 = 0; i1 < N; i1++ ) {
		if ( ( X[ ix1 ] != 0.0f ) || ( Y[ iy1 ] != 0.0f ) ) {
			tmp1 = alpha * Y[ iy1 ];
			tmp2 = alpha * X[ ix1 ];
			ix0 = ix1;
			iy0 = iy1;
			for ( i0 = i1; i0 < N; i0++ ) {
				A[ (LDA*i1)+i0 ] += ( X[ ix0 ] * tmp1 ) + ( Y[ iy0 ] * tmp2 ); // eslint-disable-line max-len
				ix0 += strideX;
				iy0 += strideY;
			}
		}
		ix1 += strideX;
		iy1 += strideY;
	}
	return;
}
