'use strict';

var Complex128 = require( '@stdlib/complex/float64' );
var parseComplex128 = require( './../lib' );

var str = '5 + 3j';
try {
	var z = parseComplex128( str );
	console.log( z );
	// => <Complex128>

	var bool = ( z instanceof Complex128 );
	console.log( bool );
	// => true

	bool = ( z.re === 5.0 );
	console.log( bool );
	// => true

	bool = ( z.im === 3.0 );
	console.log( bool );
	// => true
} catch (error) {
	console.error('An error occurred:', error.message);
}
