// TypeScript Version: 2.0

/**
* Smallest positive half-precision floating-point normal number.
*
* @example
* var smallest = FLOAT16_SMALLEST_NORMAL;
* // returns 6.103515625e-5
*/
declare const FLOAT16_SMALLEST_NORMAL: number;


// EXPORTS //

export = FLOAT16_SMALLEST_NORMAL;
