'use strict';

// MODULES //

var tape = require( 'tape' );
var pow = require( '@stdlib/math/base/special/pow' );
var FLOAT32_SMALLEST_SUBNORMAL = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof FLOAT32_SMALLEST_SUBNORMAL, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value equals the smallest single-precision floating-point denormalized value', function test( t ) {
	t.equal( FLOAT32_SMALLEST_SUBNORMAL, pow(2, -126)*pow(2, -23), 'equals 2**-126 * 2**-23' );
	t.end();
});
