'use strict';

// MODULES //

var tape = require( 'tape' );
var pow = require( '@stdlib/math/base/special/pow' );
var INT16_MAX = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof INT16_MAX, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 2**15 - 1', function test( t ) {
	t.equal( INT16_MAX, pow(2, 15) - 1, 'equals 2**15 - 1' );
	t.end();
});
