// TypeScript Version: 2.0

/**
* Maximum Unicode code point in the Basic Multilingual Plane (BMP).
*
* @example
* var bool = ( UNICODE_MAX_BMP === 65535 );
* // returns true
*/
declare const UNICODE_MAX_BMP: number;


// EXPORTS //

export = UNICODE_MAX_BMP;
