'use strict';

/**
* Swaps array elements. Note that elements are swapped in-place; i.e., the input array is mutated.
*
* @private
* @param {(Array|TypedArray)} arr - input array
* @param {uinteger32} i - element index
* @param {uinteger32} j - element index
* @returns {(Array|TypedArray)} input array
*
* @example
* var arr = [ 1, 2, 3 ];
*
* var out = swap( arr, 1, 2 );
* // returns [ 1, 3, 2 ]
*/
function swap( arr, i, j ) {
	var tmp = arr[ i ];
	arr[ i ] = arr[ j ];
	arr[ j ] = tmp;
	return arr;
}


// EXPORTS //

module.exports = swap;
