'use strict';

// MODULES //

var data = require( './../data/words.json' );


// MAIN //

/**
* Returns a list of negative opinion words.
*
* @returns {StringArray} words
*
* @example
* var list = words();
* // returns [ '2-faced', '2-faces', 'abnormal', 'abolish', ... ]
*/
function words() {
	return data.slice();
} // end FUNCTION words()


// EXPORTS //

module.exports = words;
