tape( 'the command-line interface prints Moby Dick (plain text)', opts, function test( t ) {
	var opts;
	var cmd;

	cmd = [
		process.execPath,
		fpath
	];

	opts = {
		'maxBuffer': 1500*1024
	};

	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			stdout = stdout.toString();
			t.strictEqual( isString( stdout ), true, 'prints plain text' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});

tape( 'the command-line interface prints Moby Dick (newline-delimited JSON)', opts, function test( t ) {
	var expected;
	var opts;
	var cmd;

	cmd = [
		process.execPath,
		fpath,
		'--format ndjson'
	];

	expected = require( './../lib/' );

	opts = {
		'maxBuffer': 1500*1024
	};

	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		var str;
		var i;
		if ( error ) {
			t.fail( error.message );
		} else {
			stdout = stdout.toString().split( RE_EOL );
			for ( i = 0; i < expected.length; i++ ) {
				str = JSON.stringify( expected[ i ] );
				t.strictEqual( stdout[ i ], str, 'returns expected JSON string' );
			}
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});
