/* eslint-disable no-sync */
'use strict';

// MODULES //

var fs = require( 'fs' );
var join = require( 'path' ).join;
var isBrowser = require( '@stdlib/assert/is-browser' );
var readDir = require( '@stdlib/fs/read-dir' ).sync;
var extname = require( '@stdlib/utils/extname' );
var dataDir = require( './data_dir.js' );
var RE = require( './re_filename.js' );


// VARIABLES //

var list;


// FUNCTIONS //

/**
* Generates a sorted list of JSON data file filenames by reading the file system.
*
* @private
* @returns {StringArray} filenames
*/
function read() {
	var fpath;
	var files;
	var stats;
	var out;
	var f;
	var i;

	files = readDir( dataDir );
	out = [];
	for ( i = 0; i < files.length; i++ ) {
		f = files[ i ];
		if ( extname( f ) === '.json' && RE.test( f ) ) {
			fpath = join( dataDir, f );
			stats = fs.statSync( fpath );
			if ( stats.isFile() ) {
				out.push( f );
			}
		}
	}
	return out.sort();
} // end FUNCTION read()


// MAIN //

if ( isBrowser ) {
	list = require( './browser_file_list.json' );
} else {
	list = read();
}


// EXPORTS //

module.exports = list;
