'use strict';

// MODULES //

var tape = require( 'tape' );
var isObjectArray = require( '@stdlib/assert/is-plain-object' ).isPlainObjectArray;
var db = require( './../lib/browser_db.js' );


// TESTS //

tape( 'main export is an object array', function test( t ) {
	t.ok( true, __filename );
	t.equal( isObjectArray( db ), true, 'main export is an object array' );
	t.end();
});

tape( 'as of 2016, export has a length greater than or equal to 228', function test( t ) {
	t.equal( db.length >= 228, true, 'has length greater than or equal to 228 (as of 2016)' );
	t.end();
});
