'use strict';

// MODULES //

var tape = require( 'tape' );
var isObject = require( '@stdlib/assert/is-plain-object' );
var isPositiveIntegerArray = require( '@stdlib/assert/is-positive-integer-array' ).primitives;
var index = require( './../lib/index_year.js' );


// TESTS //

tape( 'main export is a object', function test( t ) {
	t.ok( true, __filename );
	t.equal( isObject( index ), true, 'main export is an object' );
	t.end();
});

tape( 'export maps years to data file indices', function test( t ) {
	t.equal( isPositiveIntegerArray( index[ 2008 ] ), true, 'returns an array of indices' );
	t.end();
});
