// TypeScript Version: 2.0

/**
* Returns an error message corresponding to a provided two-character error identifier.
*
* @param id - error identifier
* @returns error message
*
* @example
* var v = id2msg( '8t' );
* // returns <string>
*/
declare function id2msg( id: string ): string | null;


// EXPORTS //

export = id2msg;
