'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var database = require( '@stdlib/error/tools/database' );
var format = require( '@stdlib/string/format' );


// VARIABLES //

var db;


// MAIN //

/**
* Returns an error message corresponding to a provided two-character error identifier.
*
* @param {string} id - error identifier
* @throws {TypeError} must provide a string
* @returns {(string|null)} error message
*
* @example
* var v = id2msg( '9b' );
* // returns <string>
*
* @example
* var v = id2msg( 'boop' );
* // returns null
*/
function id2msg( id ) {
	if ( !isString( id ) ) {
		throw new TypeError( format( 'invalid argument. Must provide a string. Value: `%s`.', id ) );
	}
	if ( !db ) {
		db = database();
	}
	if ( hasOwnProp( db, id ) ) {
		return db[ id ];
	}
	return null;
}


// EXPORTS //

module.exports = id2msg;
