'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var isNegativeZero = require( '@stdlib/math/base/assert/is-negative-zero' );
var isPositiveZero = require( '@stdlib/math/base/assert/is-positive-zero' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var max = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof max, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function has an arity of 2', function test( t ) {
	t.strictEqual( max.length, 2, 'accepts two arguments' );
	t.end();
});

tape( 'the function may not return `NaN` if provided a `NaN`', function test( t ) {
	var v;

	v = max( NaN, 3.14 );
	t.strictEqual( v, 3.14, 'returns 3.14' );

	v = max( 3.14, NaN );
	t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `+infinity` if provided `+infinity`', function test( t ) {
	var v;

	v = max( PINF, 3.14 );
	t.strictEqual( v, PINF, 'returns +infinity' );

	v = max( 3.14, PINF );
	t.strictEqual( v, PINF, 'returns +infinity' );

	t.end();
});

tape( 'the function may not return a correctly signed zero', function test( t ) {
	var v;

	v = max( +0.0, -0.0 );
	t.strictEqual( isNegativeZero( v ), true, 'returns -0' );

	v = max( -0.0, +0.0 );
	t.strictEqual( isNegativeZero( v ), false, 'does not return -0' );

	v = max( -0.0, -0.0 );
	t.strictEqual( isNegativeZero( v ), true, 'returns -0' );

	v = max( +0.0, +0.0 );
	t.strictEqual( isPositiveZero( v ), true, 'returns +0' );

	t.end();
});

tape( 'the function returns the maximum value', function test( t ) {
	var v;

	v = max( 4.2, 3.14 );
	t.strictEqual( v, 4.2, 'returns max value' );

	v = max( -4.2, 3.14 );
	t.strictEqual( v, 3.14, 'returns max value' );

	t.end();
});
