'use strict';

// MAIN //

/**
* Returns the minimum value.
*
* @param {number} x - first number
* @param {number} y - second number
* @returns {number} minimum value
*
* @example
* var v = min( 3.14, 4.2 );
* // returns 3.14
*
* @example
* var v = min( 3.14, NaN );
* // returns NaN
*
* @example
* var v = min( NaN, 3.14 );
* // returns 3.14
*
* @example
* var v = min( -0.0, +0.0 );
* // returns +0.0
*
* @example
* var v = min( +0.0, -0.0 );
* // returns -0.0
*/
function min( x, y ) {
	if ( x < y ) {
		return x;
	}
	return y;
} // end FUNCTION min()


// EXPORTS //

module.exports = min;
