'use strict';

/**
* Create a directory.
*
* @module @stdlib/fs/mkdir
*
* @example
* var mkdir = require( '@stdlib/fs/mkdir' );
*
* var opts = {
*    'mode': parseInt( '755', 8 ),
*    'recursive': true
* };
*
* mkdir( './foo/bar', opts, onDir );
*
* function onDir( error, path ) {
*     if ( error ) {
*         throw error;
*     }
*	  console.log( path );
* }
*
* @example
* var mkdir = require( '@stdlib/fs/mkdir' );
*
* mkdir.sync( './foo/bar' );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var mkdir = require( './async.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( mkdir, 'sync', sync );


// EXPORTS //

module.exports = mkdir;
