import readNDJSON = require( './index' );


// TESTS //

/**
* Call back function.
*
* @param {Error|null} error - error
* @param {string|Buffer} file - file path
* @returns {None} - none
*
*/

function onLoad( error: Error | null, file: string | Buffer ): void {
	if ( error || !file ) {
		throw error;
	}
};


// TESTS //

// The function does not have a return value...
{
	readNDJSON( 'package.json', onLoad ); // $ExpectType void
}

// The compiler throws an error if the function is provided a first argument which is not a string, buffer, or file descriptor...
{
	readNDJSON( false, onLoad ); // $ExpectError
	readNDJSON( true, onLoad ); // $ExpectError
	readNDJSON( null, onLoad ); // $ExpectError
	readNDJSON( undefined, onLoad ); // $ExpectError
	readNDJSON( [], onLoad ); // $ExpectError
	readNDJSON( {}, onLoad ); // $ExpectError
	readNDJSON( ( x: number ): number => x, onLoad ); // $ExpectError
}

// The compiler throws an error if the function is provided a callback argument which is not a function with the expected signature...
{
	readNDJSON( '/path/to/package.json', 'abc' ); // $ExpectError
	readNDJSON( '/path/to/package.json', 1 ); // $ExpectError
	readNDJSON( '/path/to/package.json', false ); // $ExpectError
	readNDJSON( '/path/to/package.json', true ); // $ExpectError
	readNDJSON( '/path/to/package.json', null ); // $ExpectError
	readNDJSON( '/path/to/package.json', undefined ); // $ExpectError
	readNDJSON( '/path/to/package.json', [] ); // $ExpectError
	readNDJSON( '/path/to/package.json', {} ); // $ExpectError
	readNDJSON( '/path/to/package.json', ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided an options argument which is not an object or string...
{
	readNDJSON( 'package.json', false, onLoad ); // $ExpectError
	readNDJSON( 'package.json', true, onLoad ); // $ExpectError
	readNDJSON( 'package.json', null, onLoad ); // $ExpectError
	readNDJSON( 'package.json', undefined, onLoad ); // $ExpectError
	readNDJSON( 'package.json', 123, onLoad ); // $ExpectError
	readNDJSON( 'package.json', [], onLoad ); // $ExpectError
	readNDJSON( 'package.json', ( x: number ): number => x, onLoad ); // $ExpectError
}

// The compiler throws an error if the function is provided an `encoding` option which is not a string or null...
{
	readNDJSON( 'package.json', { 'encoding': 123 }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'encoding': true }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'encoding': false }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'encoding': [] }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'encoding': {} }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'encoding': ( x: number ): number => x }, onLoad ); // $ExpectError
}

// The compiler throws an error if the function is provided a `flag` option which is not a string...
{
	readNDJSON( 'package.json', { 'flag': 123 }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'flag': true }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'flag': false }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'flag': null }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'flag': [] }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'flag': {} }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'flag': ( x: number ): number => x }, onLoad ); // $ExpectError
}

// The compiler throws an error if the function is provided a `reviver` option which is not a function...
{
	readNDJSON( 'package.json', { 'reviver': 123 }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'reviver': 'abc' }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'reviver': true }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'reviver': false }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'reviver': null }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'reviver': [] }, onLoad ); // $ExpectError
	readNDJSON( 'package.json', { 'reviver': {} }, onLoad ); // $ExpectError
}

// The compiler throws an error if the function is provided an unsupported number of arguments...
{
	readNDJSON(); // $ExpectError
	readNDJSON( 'C:\\foo\\bar\\baz\\package.json' ); // $ExpectError
}

// Attached to main export is a `sync` method which returns a string or an error...
{
	readNDJSON.sync( 'package.json' ); // $ExpectType string | Error
}

// The compiler throws an error if the `sync` method is provided a first argument which is not a string, buffer, or file descriptor...
{
	readNDJSON.sync( false ); // $ExpectError
	readNDJSON.sync( true ); // $ExpectError
	readNDJSON.sync( null ); // $ExpectError
	readNDJSON.sync( undefined ); // $ExpectError
	readNDJSON.sync( [] ); // $ExpectError
	readNDJSON.sync( {} ); // $ExpectError
	readNDJSON.sync( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the `sync` method is provided an options argument which is not an object or string...
{
	readNDJSON.sync( 'package.json', null ); // $ExpectError
	readNDJSON.sync( 'package.json', true ); // $ExpectError
	readNDJSON.sync( 'package.json', false ); // $ExpectError
	readNDJSON.sync( 'package.json', 123 ); // $ExpectError
	readNDJSON.sync( 'package.json', [] ); // $ExpectError
	readNDJSON.sync( 'package.json', ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the `sync` method is provided an `encoding` option which is not a string or null...
{
	readNDJSON.sync( 'package.json', { 'encoding': 123 } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'encoding': true } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'encoding': false } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'encoding': [] } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'encoding': {} } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'encoding': ( x: number ): number => x } ); // $ExpectError
}

// The compiler throws an error if the `sync` method is provided a `flag` option which is not a string...
{
	readNDJSON.sync( 'package.json', { 'flag': 123 } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'flag': true } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'flag': false } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'flag': null } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'flag': [] } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'flag': {} } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'flag': ( x: number ): number => x } ); // $ExpectError
}

// The compiler throws an error if the `sync` method is provided a `reviver` option which is not a function...
{
	readNDJSON.sync( 'package.json', { 'reviver': 'abc' } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'reviver': 123 } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'reviver': true } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'reviver': false } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'reviver': null } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'reviver': [] } ); // $ExpectError
	readNDJSON.sync( 'package.json', { 'reviver': {} } ); // $ExpectError
}

// The compiler throws an error if the `sync` method is provided an unsupported number of arguments...
{
	readNDJSON.sync(); // $ExpectError
}
