'use strict';

/**
* Resolve paths from a set of paths according to a predicate function by walking parent directories.
*
* @module @stdlib/fs/resolve-parent-paths-by
*
* @example
* var resolveParentPathsBy = require( '@stdlib/fs/resolve-parent-paths-by' );
*
* resolveParentPathsBy( [ 'package.json', 'package-lock.json' ], predicate, onPaths );
*
* function predicate( path, next ) {
*     next( null, true );
* }
*
* function onPaths( error, paths ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( paths );
* }
*
* @example
* var resolveParentPathsBy = require( '@stdlib/fs/resolve-parent-paths-by' );
*
* function predicate() {
*     return true;
* }
*
* var paths = resolveParentPathsBy.sync( [ 'package.json', 'package-lock.json' ], predicate );
* // e.g., returns [...]
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var main = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( main, 'sync', sync );


// EXPORTS //

module.exports = main;

// exports: { "sync": "main.sync" }
