'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var iteratorSymbol = require( '@stdlib/symbol/iterator' );
var floor = require( '@stdlib/math/base/special/floor' );
var isCollection = require('@stdlib/assert/is-collection');
var resolveGetter = require( '@stdlib/array/base/resolve-getter' );


// MAIN //

/**
* Returns an iterator which generates the Cartesian square of an input array-like object.
*
* @param {Collection} arr - input array
* @throws {TypeError} must provide an array-like object
* @returns {Iterator} iterator
*
* @example
* var iter = iterCartesianSquare( [ 'a', 'b', 'c' ] );
*
* var v = iter.next().value;
* // returns [ 'a', 'a' ]
*
* v = iter.next().value;
* // returns [ 'a', 'b' ]
*
* // ...
*/
function iterCartesianSquare( arr ) {
	var iter;
	var FLG;
	var get;
	var i;

	if ( !isCollection( arr ) ) {
		throw new TypeError( 'invalid argument. Must provide an array-like object. Value: `' + arr + '`.' );
	}

	get = resolveGetter( arr );

	iter = {};
	FLG = false;
	i = 0;
	setReadOnly( iter, 'next', next );
	setReadOnly( iter, 'return', finish );

	if ( iteratorSymbol ) {
		setReadOnly( iter, iteratorSymbol, factory );
	}
	return iter;
	/**
	* Returns an iterator protocol-compliant object containing the next iterated value.
	* @private
	* @returns {Object} iterator result
	*/
	function next() {
		var v;
		var N;
		if ( FLG ) {
			return {
				'done': true
			};
		}
		N = arr.length * arr.length;
		v = [get(arr, floor(i / arr.length)), get(arr, i % arr.length)];
		i += 1;
		if ( i >= N ) {
			FLG = true;
		}
		return {
			'value': v,
			'done': false
		};
	}

	/**
	* Finishes an iterator.
	* @private
	* @returns {Object} iterator result
	*/
	function finish() {
		FLG = true;
		return {
			'done': true
		};
	}

	/**
	* Returns a new iterator.
	* @private
	* @returns {Iterator} iterator
	*/
	function factory() {
		return iterCartesianSquare( arr );
	}
}


// EXPORTS //

module.exports = iterCartesianSquare;
