'use strict';

var randu = require( '@stdlib/random/iter/randu' );
var iterMap = require( './../lib' );

var rand;
var it;
var r;

function scale( v ) {
	return v * 10.0;
}

// Create a seeded iterator for generating pseudorandom numbers:
rand = randu({
	'seed': 1234,
	'iter': 10
});

// Create an iterator which scales the generated numbers:
it = iterMap( rand, scale );

// Perform manual iteration...
while ( true ) {
	r = it.next();
	if ( r.done ) {
		break;
	}
	console.log( r.value );
}
