// TypeScript Version: 4.1

/// <reference types="@stdlib/types"/>
/**
* Interface describing `dlae2`.
*/
interface Routine {
	/**
	* Computes the eigenvalues of a 2x2 symmetric matrix.
	*
	* @param A - the (0,0) element of a 2x2 symmetric matrix
	* @param B - the (0,1) and (1,0) element of a 2x2 symmetric matrix
	* @param C - the (1,1) element of a 2x2 symmetric matrix
	* @param out - output array containing eigenvalues of the larger and smaller absolute values, respectively
	* @returns output array
	*
	* @example
	* var Float64Array = require( '@stdlib/array/float64' );
	*
	* var out = new Float64Array( 2 );
	* out = dlae2( 2.0, 3.0, 4.0, out );
	* // out => <Float64Array>[ ~6.162, ~-0.162 ]
	*/
	( A: number, B: number, C: number, out: Float64Array ): Float64Array;

	/**
	* Computes the eigenvalues of a 2x2 symmetric matrix using alternative indexing semantics.
	*
	* @type {Function}
	* @param A - the (0,0) element of a 2x2 symmetric matrix
	* @param B - the (0,1) and (1,0) element of a 2x2 symmetric matrix
	* @param C - the (1,1) element of a 2x2 symmetric matrix
	* @param out - output array containing eigenvalues of the larger and smaller absolute values, respectively
	* @param strideOut - stride length for `out`
	* @param offsetOut - starting index of `out`
	* @returns output array
	*
	* @example
	* var Float64Array = require( '@stdlib/array/float64' );
	*
	* var out = new Float64Array( 2 );
	* out = dlae2.ndarray( 2.0, 3.0, 4.0, out, 1, 0 );
	* // out => <Float64Array>[ ~6.162, ~-0.162 ]
	*/
	ndarray( A: number, B: number, C: number, out: Float64Array, strideOut: number, offsetOut: number ): Float64Array;
}

/**
* Computes the eigenvalues of a 2x2 symmetric matrix.
*
* @param A - the (0,0) element of a 2x2 symmetric matrix
* @param B - the (0,1) and (1,0) element of a 2x2 symmetric matrix
* @param C - the (1,1) element of a 2x2 symmetric matrix
* @param out - output array containing eigenvalues of the larger and smaller absolute values, respectively
* @returns output array
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var out = new Float64Array( 2 );
* out = dlae2( 2.0, 3.0, 4.0, out );
* // out => <Float64Array>[ ~6.162, ~-0.162 ]
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var out = new Float64Array( 2 );
* out = dlae2.ndarray( 2.0, 3.0, 4.0, out, 1, 0 );
* // out => <Float64Array>[ ~6.162, ~-0.162 ]
*/
declare var dlae2: Routine;


// EXPORTS //

export = dlae2;
