'use strict';

// MODULES //

var sqrt = require( '@stdlib/math/base/special/sqrt' );
var abs = require( '@stdlib/math/base/special/abs' );
var pow = require( '@stdlib/math/base/special/pow' );


// MAIN //

/**
* Computes the eigenvalues of a 2x2 symmetric matrix.
*
* @private
* @param {number} A - the (0,0) element of a 2x2 symmetric matrix
* @param {number} B - the (0,1) and (1,0) element of a 2x2 symmetric matrix
* @param {number} C - the (1,1) element of a 2x2 symmetric matrix
* @param {Float64Array} out - output array containing eigenvalues of the larger and smaller absolute values, respectively
* @param {integer} strideOut - stride length for `out`
* @param {NonNegativeInteger} offsetOut - starting index of `out`
* @returns {Float64Array} output array
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var out = new Float64Array( 2 );
* out = dlae2( 2.0, 3.0, 4.0, out, 1, 0 );
* // out => <Float64Array>[ ~6.162, ~-0.162 ]
*/
function dlae2( A, B, C, out, strideOut, offsetOut ) {
	var acmx;
	var acmn;
	var adf;
	var rt1;
	var rt2;
	var ab;
	var df;
	var sm;
	var rt;
	var tb;

	sm = A + C;
	df = A - C;
	adf = abs( df );
	tb = B + B;
	ab = abs( tb );
	if ( abs( A ) > abs( C ) ) {
		acmx = A;
		acmn = C;
	} else {
		acmx = C;
		acmn = A;
	}
	if ( adf > ab ) {
		rt = adf * sqrt( 1.0 + pow( ( ab / adf ), 2 ) );
	} else if ( adf < ab ) {
		rt = ab * sqrt( 1.0 + pow( ( adf / ab ), 2 ) );
	} else {
		// Includes case AB = ADF = 0.0
		rt = ab * sqrt( 2.0 );
	}
	if ( sm < 0.0 ) {
		rt1 = 0.5 * ( sm - rt );

		// Order of execution is important. To get fully accurate smaller eigenvalue, next line needs to be executed in higher precision.
		rt2 = ( ( acmx / rt1 ) * acmn ) - ( ( B / rt1 ) * B );
	} else if ( sm > 0.0 ) {
		rt1 = 0.5 * ( sm + rt );

		// Order of execution is important. To get fully accurate smaller eigenvalue, next line needs to be executed in higher precision.
		rt2 = ( ( acmx / rt1 ) * acmn ) - ( ( B / rt1 ) * B );
	} else {
		// Includes case RT1 = RT2 = 0.0
		rt1 = 0.5 * rt;
		rt2 = -0.5 * rt;
	}
	out[ offsetOut ] = rt1;
	out[ offsetOut + strideOut ] = rt2;
	return out;
}


// EXPORTS //

module.exports = dlae2;
