'use strict';

// MODULES //

var EPS = require( '@stdlib/constants/float64/eps' );
var SMALLEST_NORMAL = require( '@stdlib/constants/float64/smallest-normal' );
var MIN_EXPONENT = require( '@stdlib/constants/float64/min-base2-exponent' );
var MAX_EXPONENT = require( '@stdlib/constants/float64/max-base2-exponent' );
var lowercase = require( '@stdlib/string/base/lowercase' );


// VARIABLES //

var DIGITS_ZERO = 53;
var RADIX_ZERO = 2.0;
var HUGE_ZERO = 1.7976931348623157E+308;


// MAIN //

/**
* Determines double-precision machine parameters.
*
* @param {string} cmach - specifies the value to be returned
* @returns {number} machine parameter
*
* @example
* var out = dlamch( 'E' );
* // returns ~1.1102230246251565E-016
*
* out = dlamch( 'S' );
* // returns ~2.2250738585072014E-308
*
* out = dlamch( 'B' );
* // returns 2.0
*/
function dlamch( cmach ) {
	var sfmin;
	var small;
	var eps;
	var rnd;

	rnd = 1.0;
	if ( rnd === 1.0 ) {
		eps = EPS * 0.5;
	} else {
		eps = EPS;
	}

	cmach = lowercase( cmach.charAt( 0 ) );

	if ( cmach === 'e' ) {
		return eps;
	}
	if ( cmach === 's' ) {
		sfmin = SMALLEST_NORMAL;
		small = 1.0 / HUGE_ZERO;
		if ( small >= sfmin ) {
			sfmin = small * ( 1.0 + eps );
		}
		return sfmin;
	}
	if ( cmach === 'b' ) {
		return RADIX_ZERO;
	}
	if ( cmach === 'p' ) {
		return eps * RADIX_ZERO;
	}
	if ( cmach === 'n' ) {
		return DIGITS_ZERO;
	}
	if ( cmach === 'r' ) {
		return rnd;
	}
	if ( cmach === 'm' ) {
		return MIN_EXPONENT + 1;
	}
	if ( cmach === 'u' ) {
		return SMALLEST_NORMAL;
	}
	if ( cmach === 'l' ) {
		return MAX_EXPONENT + 1;
	}
	if ( cmach === 'o' ) {
		return HUGE_ZERO;
	}
	return 0.0;
}


// EXPORTS //

module.exports = dlamch;
