tape( 'the function allows accessing elements from non contiguous arrangement of row and column (row-major)', function test( t ) {
	var expected;
	var out;
	var A;

	/* eslint-disable array-element-newline, no-multi-spaces */

	A = new Float64Array([
		999, 999, 999, 999, 999, 999,
		999,   1, 999,   2, 999,   3,
		999, 999, 999, 999, 999, 999,
		999,   0, 999,   4, 999,   5,
		999, 999, 999, 999, 999, 999,
		999,   0, 999,   0, 999,   6,
		999, 999, 999, 999, 999, 999
	]);
	expected = new Float64Array([
		999, 999, 999, 999, 999, 999,
		999, 3.7, 999, 3.1, 999, 3.1,
		999, 999, 999, 999, 999, 999,
		999,   0, 999, 3.7, 999, 3.1,
		999, 999, 999, 999, 999, 999,
		999,   0, 999,   0, 999, 3.7,
		999, 999, 999, 999, 999, 999
	]);

	out = dlaset( 'upper', 3, 3, 3.1, 3.7, A, 12, 2, 7 );
	t.strictEqual( out, A, 'returns expected value' );
	isApprox( t, A, expected, 2.0 );
	t.end();
});

tape( 'the function allows access elements from non contiguous arrangement of row and column in reverse order (row-major)', function test( t ) {
	var expected;
	var out;
	var A;

	/* eslint-disable array-element-newline, no-multi-spaces */

	A = new Float64Array([
		999, 999, 999, 999, 999, 999,
		999,   1, 999,   2, 999,   3,
		999, 999, 999, 999, 999, 999,
		999,   0, 999,   4, 999,   5,
		999, 999, 999, 999, 999, 999,
		999,   0, 999,   0, 999,   6,
		999, 999, 999, 999, 999, 999
	]);
	expected = new Float64Array([
		999, 999, 999, 999, 999, 999,
		999, 3.7, 999,   2, 999,   3,
		999, 999, 999, 999, 999, 999,
		999, 3.1, 999, 3.7, 999,   5,
		999, 999, 999, 999, 999, 999,
		999, 3.1, 999, 3.1, 999, 3.7,
		999, 999, 999, 999, 999, 999
	]);

	out = dlaset( 'upper', 3, 3, 3.1, 3.7, A, -12, -2, 35 );
	t.strictEqual( out, A, 'returns expected value' );
	isApprox( t, A, expected, 2.0 );
	t.end();
});
