// TypeScript Version: 4.1

/// <reference types="@stdlib/types"/>

import { Layout, MatrixTriangle } from '@stdlib/types/blas';

/**
* Interface describing `dlauu2`.
*/
interface Routine {
	/**
	* Computes the product `U*U^T` or `L^T*L`, where the triangular factor `U` or `L` is stored in the upper or lower triangular part of array `A`.
	*
	* @param order - storage layout
	* @param uplo - specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
	* @param N - order of triangular factor `U` or `L`.
	* @param A - input array
	* @param LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
	* @returns status code
	*
	* @example
	* var Float64Array = require( '@stdlib/array/float64' );
	*
	* var A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
	* var out = dlauu2( 'row-major', 'upper', 2, A, 2 );
	* // returns 0
	*/
	( order: Layout, uplo: MatrixTriangle, N: number, A: Float64Array, LDA: number ): number;

	/**
	* Computes the product `U*U^T` or `L^T*L`, where the triangular factor `U` or `L` is stored in the upper or lower triangular part of array `A` using alternative indexing semantics.
	*
	* @param uplo - specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
	* @param N - order of triangular factor `U` or `L`.
	* @param A - input array
	* @param strideA1 - stride of the first dimension of `A`
	* @param strideA2 - stride of the second dimension of `A`
	* @param offsetA - starting index for `A`
	* @returns status code
	*
	* @example
	* var Float64Array = require( '@stdlib/array/float64' );
	*
	* var A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
	* var out = dlauu2.ndarray( 'upper', 2, A, 2, 1, 0 );
	* // returns 0
	*/
	ndarray( uplo: MatrixTriangle, N: number, A: Float64Array, strideA1: number, strideA2: number, offsetA: number ): number;
}

/**
* Computes the product `U*U^T` or `L^T*L`, where the triangular factor `U` or `L` is stored in the upper or lower triangular part of array `A`.
*
* @param order - storage layout
* @param uplo - specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
* @param N - order of triangular factor `U` or `L`.
* @param A - input array
* @param LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @returns status code
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
* var out = dlauu2( 'row-major', 'upper', 2, A, 2 );
* // returns 0
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
* var out = dlauu2.ndarray( 'upper', 2, A, 2, 1, 0 );
* // returns 0
*/
declare var dlauu2: Routine;


// EXPORTS //

export = dlauu2;
