// TypeScript Version: 4.1

/// <reference types="@stdlib/types"/>

import { Layout, MatrixTriangle } from '@stdlib/types/blas';

/**
* Status code.
*
* ## Notes
*
* The status code indicates the following conditions:
*
* -   if equal to zero, then the factorization was successful.
* -   if less than zero, then the k-th argument had an illegal value, where `k = -StatusCode`.
* -   if greater than zero, then the leading principal minor of order `k` is not positive, where `k = StatusCode`. If `k < N`, then the factorization could not be completed. If `k = N`, then the factorization was completed, but `D(N) <= 0`, meaning that the matrix `A` is not positive definite.
*/
type StatusCode = number;

/**
* Interface describing `dpotf2`.
*/
interface Routine {
	/**
	* Computes the Cholesky factorization of a real symmetric positive definite matrix `A`.
	*
	* @param order - storage layout
	* @param uplo - specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
	* @param N - order of matrix `A`
	* @param A - input symmetric positive definite matrix
	* @param LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
	* @returns status code
	*
	* @example
	* var Float64Array = require( '@stdlib/array/float64' );
	*
	* var A = new Float64Array( [ 1.0, 0.0, 0.0, 4.0 ] );
	*
	* var out = dpotf2( 'row-major', 'upper', 2, A, 2 );
	* // returns 0
	*/
	( order: Layout, uplo: MatrixTriangle, N: number, A: Float64Array, LDA: number ): StatusCode;

	/**
	* Computes the Cholesky factorization of a real symmetric positive definite matrix `A` using alternative semantic indexing.
	*
	* @param uplo - specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
	* @param N - order of matrix `A`
	* @param A - input symmetric positive definite matrix
	* @param strideA1 - stride of the first dimension of `A`
	* @param strideA2 - stride of the second dimension of `A`
	* @param offsetA - starting index for `A`
	* @returns status code
	*
	* @example
	* var Float64Array = require( '@stdlib/array/float64' );
	*
	* var A = new Float64Array( [ 1.0, 0.0, 0.0, 4.0 ] );
	*
	* var out = dpotf2.ndarray( 'upper', 2, A, 2, 1, 0 );
	* // returns 0
	*/
	ndarray( uplo: MatrixTriangle, N: number, A: Float64Array, strideA1: number, strideA2: number, offsetA: number ): StatusCode;
}

/**
* Computes the Cholesky factorization of a real symmetric positive definite matrix `A`.
*
* @param order - storage layout
* @param uplo - specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
* @param N - order of matrix `A`
* @param A - input symmetric positive definite matrix
* @param LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @returns status code
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var A = new Float64Array( [ 1.0, 0.0, 0.0, 4.0 ] );
*
* var out = dpotf2( 'row-major', 'upper', 2, A, 2 );
* // returns 0
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var A = new Float64Array( [ 1.0, 0.0, 0.0, 4.0 ] );
*
* var out = dpotf2.ndarray( 'upper', 2, A, 2, 1, 0 );
* // returns 0
*/
declare var dpotf2: Routine;


// EXPORTS //

export = dpotf2;
