'use strict';

/**
* LAPACK routine to compute the inverse of a triangular matrix.
*
* @module @stdlib/lapack/base/dtrti2
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
* var dtrti2 = require( '@stdlib/lapack/base/dtrti2' );
*
* var A = new Float64Array( [ 1.0, 2.0, 0.0, 4.0 ] );
* dtrti2( 'row-major', 'upper', 'non-unit', 2, A, 2 );
* // A => <Float64Array>[ 1.0, -0.5, 0.0, 0.25 ]
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
* var dtrti2 = require( '@stdlib/lapack/base/dtrti2' );
*
* var A = new Float64Array( [ 1.0, 2.0, 0.0, 4.0 ] );
* dtrti2.ndarray( 'upper', 'non-unit', 2, A, 2, 1, 0 );
* // A => <Float64Array>[ 1.0, -0.5, 0.0, 0.25 ]
*/

// MODULES //

var join = require( 'path' ).join;
var tryRequire = require( '@stdlib/utils/try-require' );
var isError = require( '@stdlib/assert/is-error' );
var main = require( './main.js' );


// MAIN //

var dtrti2;
var tmp = tryRequire( join( __dirname, './native.js' ) );
if ( isError( tmp ) ) {
	dtrti2 = main;
} else {
	dtrti2 = tmp;
}


// EXPORTS //

module.exports = dtrti2;
