tape( 'the function correctly computes inverse of upper triangular non-unit matrix ( row-major )', function test( t ) {
	var expected;
	var out;
	var A;

	A = new Float64Array( [ 1.0, 2.0, 0.0, 4.0 ] );
	expected = new Float64Array( [ 1, -0.5, 0, 0.25 ] );
	out = dtrti2( 'row-major', 'upper', 'non-unit', 2, A, 2 );
	t.strictEqual( out, A, 'returns expected value' );
	isApprox( t, A, expected, 2.0 );
	t.end();
});

