'use strict';

// MODULES //

var tape = require( 'tape' );
var trunc = require( '@stdlib/math/base/special/trunc' );
var randu = require( '@stdlib/math/base/random/randu' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var isNonNegativeInteger = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNonNegativeInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a nonnegative integer', function test( t ) {
	var i;
	for ( i = 0; i < 101; i++ ) {
		t.equal( isNonNegativeInteger( i ), true, 'returns true when provided '+i );
	}
	t.end();
});

tape( 'the function returns `false` if not provided a nonnegative integer', function test( t ) {
	var i;
	for ( i = -1; i > -101; i-- ) {
		t.equal( isNonNegativeInteger( i ), false, 'returns false when provided '+i );
	}
	t.end();
});

tape( 'the function returns `false` if not provided an integer', function test( t ) {
	var v;
	var i;
	for ( i = 0; i < 100; i++ ) {
		v = ( randu()*100.0 ) - 50.0;
		if ( trunc(v) !== v ) {
			t.equal( isNonNegativeInteger( v ), false, 'returns false when provided '+v );
		}
	}
	t.end();
});

tape( 'the function returns `false` if provided `NaN`', function test( t ) {
	t.equal( isNonNegativeInteger( NaN ), false, 'returns false' );
	t.end();
});

tape( 'WARNING: the function returns `true` if provided `+infinity`', function test( t ) {
	t.equal( isNonNegativeInteger( PINF ), true, 'returns true' );
	t.end();
});

tape( 'the function does not distinguish between positive and negative zero and returns `true` if provided `-0`', function test( t ) {
	t.equal( isNonNegativeInteger( -0.0 ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided `-infinity`', function test( t ) {
	t.equal( isNonNegativeInteger( NINF ), false, 'returns false' );
	t.end();
});
