'use strict';

// MODULES //

var tape = require( 'tape' );
var isPositiveZero = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isPositiveZero, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided `+0`', function test( t ) {
	t.equal( isPositiveZero( 0.0 ), true, 'returns true' );
	t.equal( isPositiveZero( +0.0 ), true, 'returns true' );
	t.end();
});

tape( 'function returns `false` if provided `-0`', function test( t ) {
	t.equal( isPositiveZero( -0.0 ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided `+0`', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5.0,
		-1.0,
		NaN,
		true,
		null,
		undefined,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isPositiveZero( values[i] ), false, 'returns false' );
	}
	t.end();
});
